};

if (msg.topic.endsWith('/status')) {
  const { ison, brightness, overpower } = msg.payload;
  return {
    payload: {
      on: ison,
      brightness,
      overpower
    }
  };
}

const step = env.get('step');
const timeout = env.get('timeout');
const min = env.get('min');

let transitionUp = flow.get('transitionUp') || false;
transitionUp = !transitionUp;
flow.set('transitionUp', transitionUp);

let transitionBrightness = flow.get('transitionBrightness');

const changeBrightness = () => {
  if (transitionUp) {
    transitionBrightness += step;
    if (transitionBrightness > 100) {
      transitionBrightness = 100;
    }
  } else {
    transitionBrightness -= step;
    if (transitionBrightness < min) {
      transitionBrightness = min;
    }
  }

  node.send({
    payload: {
      brightness: transitionBrightness
    }
  });
};

changeBrightness();

flow.set('timer', setInterval(changeBrightness, timeout));

let timer = flow.get('timer') || null;

if (timer) {
  clearInterval(timer);
  timer = null;
}

flow.set('timer', timer);

const { method } = msg.payload;

if (method === 'NotifyEvent') {
  return msg;
}

node.warn('buttons in: unexpected method ' + method);

const { params: { events } } = msg.payload;

for (const ev of events) {
  const { id, event } = ev;

  node.send({
    type: 'change',
    payload: {
      input: id,
      event
    }
  });
}

const buttonId = env.get('button');
const { input } = msg.payload;

if (input === buttonId) {
  return msg;
}

